<?phprequire_once("dompdf/dompdf_config.inc.php");include "../core/ConexionMySQL.php";$id = $_GET['id'];if($id == '1' || $id == '2' || $id == '3' || $id == '4'){	$html =	  '<html><body>'.	  '<head>'.			'<link rel="stylesheet" type="text/css" href="css/pdf.css">'.		'</head>'.				'<body>';	switch($id){		case '1'://Cantidad de pasajes vendidos			$titulo = 'Cantidad de pasajes vendidos';			$query = 'SELECT COUNT(*) as cant_pasaje_vendidos FROM pasaje;';		break;		case '2'://Cantidad de pasajes vendidos por categoría:			$titulo = 'Cantidad de pasajes vendidos por categoría';			$query = 'select categoria, count(*) as cantidad '.					'from pasaje  '.					'group by categoria; ';		break;		case '3'://Cantidad de pasajes vendidos  por destino:			$titulo = 'Cantidad de pasajes vendidos  por destino';			$query = 'select a.ciudad, count(*) as cantidad '.					'from pasaje as p join vuelo as v on p.id_vuelo=v.id_vuelo join recorrido as r on v.id_recorrido=r.id_recorrido join aeropuerto as a on a.ciudad=r.id_ciudad_destino '.					'group by a.ciudad; ';		break;		case '4'://Cantidad de reservas caídas:			$titulo = 'Cantidad de reservas caídas';			$query = 'select count(*) '.					'from pasaje as p join vuelo as v on p.id_vuelo=v.id_vuelo '.					'where p.id_estado=0 and v.fecha<DATE(NOW()); ';		break;	}	$conexion = new ConexionMySQL();	$matriz = $conexion->ejecutarQuery($query);/* Desplegamos cada uno de los registros dentro de una tabla */ 	$html .=  "<h1>".$titulo."</h1>";	if(count($matriz)){		$html .=  "<table border=1 cellpadding=" . $conexion->field_count() . " cellspacing=0>";		/*Priemro los encabezados*/		$html .=  "<tr>		         <th colspan=5> ".$titulo." </th>		       <tr>";	   	foreach ($matriz[0] as $key => $value) {		         $html .=  "<th> ". $key ." </th>";	   		# code...	   	}		$html .= "</tr>";		/*Y ahora todos los registros */		for ($i=0; $i < count($matriz); $i++) {	 		$html .=  "<tr>";			$registro = $matriz[$i];		 	foreach ($registro as $key => $value) {		 		$html .=  '<td style="text-align:center vertical-align:middle">'. utf8_encode($value) ."</td>";			}	    	$html .=  "</tr>";		}		$html .=  "</table>";		$html .= '</body>'.'</body></html>';		echo $html;		$dompdf = new DOMPDF();		$dompdf->load_html($html);		$dompdf->render();		$dompdf->stream($titulo.".pdf");	}else{		$html .= "<h1>Sin Resultados</h1>";		$html .= '</body>'.'</body></html>';		echo $html;	}      }?>